import { Injectable } from '@angular/core';
import { Product } from './products.model';

@Injectable({
  providedIn: 'root'
})
export class ProductService {
  private products: Product[] = [
    { id: 1, name: 'Chicken Pickle', price: 100, description: '200gm', imageUrl: 'data:image/jpeg;base64,' },
    { id: 2, name: 'Rings', price: 150, description: '500gm', imageUrl: 'data:image/jpeg;base64,' },
    { id: 3, name: 'Murukulu', price: 150, description: '500gm', imageUrl: 'data:image/jpeg;base64,' }
  ];

  private cart: Product[] = [];

  getProducts(): Product[] {
    return this.products;
  }

  addToCart(product: Product) {
    this.cart.push(product);
  }

  getCart(): Product[] {
    return this.cart;
  }

  removeFromCart(productId: number) {
    const index = this.cart.findIndex(product => product.id === productId);
    if (index !== -1) {
      this.cart.splice(index, 1);
    }
  }
}
